head.script:
  window.digitalData = window.digitalData || [];
  window.digitalData.push({
    "page": {
          "pageID" : "/content/enicom/it/it/media/eventi/expo-dubai-2020",
          "pageUrl": "https://www.eni.com/it-IT/media/eventi/expo-dubai-2020.html",
		      "pageCategory" : "Media",
		      "pageTitle" : "Eni a Expo 2020 Dubai: Braiding the future",
		      "pageLanguage" : "it",	  
          "creationDate" : "22-11-2023 - 22:11",
          "publishDate" : "17-06-2024 - 12:33",
          "pageTagImplicit": ""
    },
    "site":{
        "siteID":"enicom",
        "env":"prod"
    }
  });

  function handleServerResponse(requestResult) {
      const internalVisitValue = requestResult;

      localStorage.setItem('vpnStatus', internalVisitValue);

      digitalData.push({
        "event": "pageView",
        "user": {
          "internalVisit": internalVisitValue
        }
      });
      console.log('Esito richiesta: ' + requestResult);
    }

    function loadImage(url, timeout) {
      return new Promise((resolve, reject) => {
        const img = new Image();
        const timer = setTimeout(() => {
          img.onload = img.onerror = null;
          reject(new Error('Timeout'));
        }, timeout);

        img.onload = () => {
          clearTimeout(timer);
          resolve(img);
        };

        img.onerror = () => {
          clearTimeout(timer);
          reject(new Error('Load failed'));
        };

        img.src = url;
      });
    }

    async function executeImageRequest(logOnly = false) {
      // Controlla se la variabile esiste e ha valore true in localStorage
      const vpnStatus = localStorage.getItem('vpnStatus');
      if (vpnStatus === 'true') {
        console.log('VPN status is active.');
        return;
      }

      const imageUrl = 'https://enicomintranet.eni.com/asset/eni-intranet-jpg.jpg';
      const timeoutDuration = 2000;

      try {
        await loadImage(imageUrl, timeoutDuration);
        if (logOnly) {
          console.log('Image loaded successfully.');
        } else {
          handleServerResponse(true);
        }
      } catch (error) {
        if (logOnly) {
          console.log('Image failed to load.');
        } else {
          handleServerResponse(false);
        }
      }
    }

    executeImageRequest();
    /*
    setTimeout(() => {
      executeImageRequest(true);
    }, 2000);
    */
